DROP TABLE IF EXISTS `IsoTimeZone`;
CREATE TABLE `IsoTimeZone`(
Id int NOT NULL PRIMARY KEY,
ZoneName varchar(100) not null,
Country varchar(100) not null,
RuleName varchar(100) not null,
GmtOffSet int not null,
Format varchar(8) not null,
StandardName varchar(100) not null
);

DROP TABLE IF EXISTS `MsTimeZone`;
CREATE TABLE `MsTimeZone`(
Id int NOT NULL PRIMARY KEY,
StandardName varchar(100) not null,
DisplayName varchar(100) not null,
DaylightName varchar(100) not null,
SupportsDaylightSavingTime bit not null,
BaseUtcOffSet int not null
);

DROP TABLE IF EXISTS `TimeZone`;
CREATE TABLE `TimeZone`(
Id int NOT NULL PRIMARY KEY,
ZoneName varchar(100) not null,
Country varchar(100) not null,
RuleName varchar(100) not null,
GmtOffSet int not null,
Format varchar(8) not null,
StandardName varchar(100) not null,
DaylightName varchar(100) not null,
DisplayName varchar(100) not null,
SupportsDaylightSavingTime bit not null,
BaseUtcOffSet int not null
);







